import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Link, Route, Routes } from "react-router-dom";
import Navbar from './Navbar'
import '../App.css'
import {Modal,ModalHeader,ModalBody,ModalFooter,} from "reactstrap";

function Favorites () {
  const dataArt = [
    { id: 1, nombre: "Rick Sanchez", anime: 'Rick and Morty' },
    { id: 2, nombre: "Morty Smith", anime: 'Rick and Morty' },
    { id: 3, nombre: "Summer Smith", anime: 'Rick and Morty' },
    { id: 4, nombre: "Beth Smith", anime: 'Rick and Morty' },
  ];
  const [data, setData] = useState(dataArt);
  const [modalEditar, setModalEditar] = useState(false);
  const [modalEliminar, setModalEliminar] = useState(false);
  const [modalInsertar, setModalInsertar] = useState(false);
  const [artSeleccionado, setArtSeleccionado] = useState({
    id: '',
    orden: '',
    cantidad: ''
  });
  const seleccionarPais=(elemento, caso)=>{
    setArtSeleccionado(elemento);
(caso==='Editar')?setModalEditar(true):setModalEliminar(true)
  }
  const handleChange=e=>{
    const {name, value}=e.target;
    setArtSeleccionado((prevState)=>({
      ...prevState,
      [name]: value
    }));
  }
  const editar=()=>{
    var dataNueva=data;
    dataNueva.map(pais=>{
      if(pais.id===artSeleccionado.id){
        pais.cantidad=artSeleccionado.cantidad;
        pais.orden=artSeleccionado.orden;
      }
    });
    setData(dataNueva);
    setModalEditar(false);
  }
  const eliminar =()=>{
    setData(data.filter(pais=>pais.id!==artSeleccionado.id));
    setModalEliminar(false);
  }
  const abrirModalInsertar=()=>{
    setArtSeleccionado(null);
    setModalInsertar(true);
  }
  const insertar =()=>{
    var valorInsertar=artSeleccionado;
    valorInsertar.id=data[data.length-1].id+1;
    var dataNueva = data;
    dataNueva.push(valorInsertar);
    setData(dataNueva);
    setModalInsertar(false);
  }
  return (
    
    <div className="App">
    <div class= "tabla">
      <h2>Escribe aquí tu personaje favorito</h2>
      <br />
    <button type="button" class="btn btn-outlinedanger" onClick={()=>abrirModalInsertar()}>Agrega tu Personaje</button>
    <br /><br />
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th>
            <th>PERSONAJE</th>
            <th>ANIME</th>
            <th>ACCIÓN</th>
          </tr>
        </thead>
        <tbody>
          {data.map(elemento=>(
            <tr>
              <td>{elemento.id}</td>
              <td>{elemento.nombre}</td>
              <td>{elemento.anime}</td>
              <td><button type="button" class="btn btn-warning" onClick={()=>seleccionarPais(elemento, 'Editar')}>Editar</button> {"   "} 
              <button type="button" class="btn btn-danger" onClick={()=>seleccionarPais(elemento, 'Eliminar')}>Eliminar</button></td>
            </tr>
          ))
          }
        </tbody>
      </table>
      
      </div>
      <Modal isOpen={modalEditar}>
        <ModalHeader>
          <div>
            <h3>Editar Personaje</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>Id</label>
            <input
              className="form-control"
              readOnly
              type="number"
              name="id"
              value={artSeleccionado && artSeleccionado.id}
            />
            <br />
            <label>Personaje</label>
            <input
              className="form-control"
              type="text"
              name="number"
              value={artSeleccionado && artSeleccionado.orden}
              onChange={handleChange}
            />
            <br />
            <label>Anime</label>
            
            <input
              className="form-control"
              type="number"
              name="cantidad"
              value={artSeleccionado && artSeleccionado.cantidad}
              onChange={handleChange}
            />
            <br />
          </div>
        </ModalBody>
        <ModalFooter>
          <button type="button" class="btn btn-outline-warning" onClick={()=>editar()}>Actualizar</button>
          <button type="button" class="btn btn-outline-danger" onClick={()=>setModalEditar(false)}>Cancelar</button>
        </ModalFooter>
      </Modal>

      <Modal isOpen={modalEliminar}>
        <ModalBody>
          Estás Seguro que deseas eliminar el personaje {artSeleccionado && artSeleccionado.orden}
        </ModalBody>
        <ModalFooter>
          <button type="button" class="btn btn-outline-secondary" onClick={()=>eliminar()}>
            Aceptar
          </button>
          <button
            className="btn btn-secondary"
            onClick={()=>setModalEliminar(false)}
          >
            Cancelar
          </button>
        </ModalFooter>
      </Modal>

        <Modal isOpen={modalInsertar}>
        <ModalHeader>
          <div>
            <h3>Agrega un nuevo Personaje</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>Id</label>
            <input
              className="form-control"
              readOnly
              type="text"
              name="id"
              value={data[data.length-1].id+1}
            />
            <br />
            <label>Personaje</label>
            <input
              className="form-control"
              type="text"
              name="orden"
              value={artSeleccionado ? artSeleccionado.orden: ''}
              onChange={handleChange}
            />
            <br />
            <label>Anime</label>
            <input
              className="form-control"
              type="text"
              name="cantidad"
              value={artSeleccionado ? artSeleccionado.cantidad: ''}
              onChange={handleChange}
            />
            <br />
          </div>
        </ModalBody>
        <ModalFooter>
          <button type="button" class="btn btn-outline-primary" onClick={()=>insertar()}>Insertar</button>
          <button type="button" class="btn btn-outline-danger" onClick={()=>setModalInsertar(false)}>Cancelar</button>
        </ModalFooter>
      </Modal>
    </div>
   
  );

  
  }



export default Favorites;
